Ext.define('Target.view.AdminGridPanel', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.admingridpanel',

    requires: [
        'Target.view.AdminGridPanelViewModel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button',
        'Ext.selection.CheckboxModel'
    ],

    viewModel: {
        type: 'admingridpanel'
    },
    id: 'admingridpanel',
    title: 'My Grid Panel',
    defaultListenerScope: true,

    bind: {
        store: '{AdminStore}'
    },
    columns: [
        {
            xtype: 'gridcolumn',
            id: 'a_account',
            dataIndex: 'a_account',
            text: '帳號',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'a_email',
            text: 'E-mail',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'a_displayname',
            text: '顯示名稱',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'a_last_login',
            text: '最後登入',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                if( value == 1){
                    return '啟用';
                }else{
                    return '停用';
                }
            },
            dataIndex: 'a_status',
            text: '啟／停用',
            flex: 0.5
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'a_created',
            text: '建立時間',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'a_modified',
            text: '最後修改',
            flex: 1
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'top',
            items: [
                {
                    xtype: 'button',
                    text: '新增',
                    listeners: {
                        click: 'onButtonClick'
                    }
                },
                {
                    xtype: 'button',
                    text: '修改',
                    listeners: {
                        click: 'onButtonClick1'
                    }
                }
            ]
        }
    ],
    selModel: {
        selType: 'checkboxmodel'
    },

    onButtonClick: function(button, e, eOpts) {
        /* 新增管理者 */
        var window = Ext.create('Target.view.AdminWindow');

        window.setConfig('title', '新增管理者');

        Ext.getCmp('adminUpdateBtn').setVisible(false);
        Ext.getCmp('adminAddBtn').setVisible(true);

        window.show();
    },

    onButtonClick1: function(button, e, eOpts) {
        /* 修改管理者 */
        var selmodel = Ext.getCmp('admingridpanel').getSelectionModel();
        var count = selmodel.getCount();

        if(count !== 0){
            var seldata = selmodel.getSelection();

            var window = Ext.create('Target.view.AdminWindow');

            Ext.getCmp('a_id').setValue(seldata[0].data.a_id);
            Ext.getCmp('a_passwordTemp').setValue(seldata[0].data.a_password);

            window.setConfig('title', '修改管理者');

            Ext.getCmp('adminForm').getForm().setValues(seldata[0].data);

            Ext.getCmp('a_account').setDisabled(true);

            Ext.getCmp('adminAddBtn').setVisible(false);
            Ext.getCmp('adminUpdateBtn').setVisible(true);

            window.show();
        }else{
            Ext.Msg.alert('訊息', '請選擇一個管理者修改');
        }

    }

});