Ext.define('Target.view.ContentGridPanel', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.contentgridpanel',

    requires: [
        'Target.view.ContentGridPanelViewModel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.toolbar.Paging',
        'Ext.button.Button',
        'Ext.selection.CheckboxModel'
    ],

    viewModel: {
        type: 'contentgridpanel'
    },
    id: 'contentgridpanel',
    title: 'My Grid Panel',
    defaultListenerScope: true,

    bind: {
        store: '{ContentStore}'
    },
    columns: [
        {
            xtype: 'gridcolumn',
            dataIndex: 'c_id',
            text: 'ID',
            flex: 0.5
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'c_desc',
            text: '描述',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'c_key',
            text: '鍵（程式判斷用）',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'c_value',
            text: '值',
            flex: 2
        }
    ],
    dockedItems: [
        {
            xtype: 'pagingtoolbar',
            dock: 'bottom',
            width: 360,
            displayInfo: true,
            bind: {
                store: '{ContentStore}'
            }
        },
        {
            xtype: 'toolbar',
            dock: 'top',
            items: [
                {
                    xtype: 'button',
                    text: '修改',
                    listeners: {
                        click: 'onButtonClick'
                    }
                }
            ]
        }
    ],
    selModel: {
        selType: 'checkboxmodel',
        mode: 'SINGLE'
    },

    onButtonClick: function(button, e, eOpts) {
        var selmodel = Ext.getCmp('contentgridpanel').getSelectionModel();
        var count = selmodel.getCount();

        if( count !== 0 ){
            var seldata = selmodel.getSelection();
            var window = Ext.create('Posh.view.ContentWindow');

            Ext.getCmp('c_id').setValue(seldata[0].data.c_id);
            window.setConfig('title', '修改文案');

            window.show();

        }else{
            Ext.Msg.alert('訊息', '請選擇一筆文案以進行修改。');
        }
    }

});