Ext.define('Target.view.ContentWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.contentwindow',

    requires: [
        'Target.view.ContentWindowViewModel',
        'Ext.form.field.Hidden',
        'Ext.form.Panel',
        'Ext.form.field.Display',
        'Ext.form.field.TextArea',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'contentwindow'
    },
    height: 420,
    id: 'contentwindow',
    width: 720,
    layout: 'fit',
    title: 'My Window',
    modal: true,
    y: 150,
    defaultListenerScope: true,

    items: [
        {
            xtype: 'hiddenfield',
            id: 'c_id',
            fieldLabel: 'Label',
            name: 'c_id'
        },
        {
            xtype: 'hiddenfield',
            id: 'cKeyTemp',
            fieldLabel: 'Label',
            name: 'c_id'
        },
        {
            xtype: 'hiddenfield',
            id: 'cValueTemp',
            fieldLabel: 'Label'
        },
        {
            xtype: 'form',
            id: 'contentForm',
            bodyPadding: 10,
            title: '',
            items: [
                {
                    xtype: 'displayfield',
                    anchor: '100%',
                    fieldLabel: '描述',
                    name: 'c_desc',
                    value: 'Display Field'
                },
                {
                    xtype: 'textareafield',
                    anchor: '98%',
                    formBind: false,
                    id: 'ckreplace',
                    name: 'c_value'
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    layout: {
                        type: 'hbox',
                        pack: 'end'
                    },
                    items: [
                        {
                            xtype: 'button',
                            text: '修改',
                            listeners: {
                                click: 'onButtonClick'
                            }
                        }
                    ]
                }
            ],
            listeners: {
                afterrender: 'onContentFormAfterRender'
            }
        }
    ],

    onButtonClick: function(button, e, eOpts) {
        // console.log(content_editor.getData());
        var cId = Ext.getCmp('c_id').getValue();
        var form = Ext.getCmp('contentForm').getForm();
        var cKeyTemp = Ext.getCmp('cKeyTemp').getValue();
        if(form.isValid()){
            if(cKeyTemp !== 'product_lastdate'){
                //console.log(content_editor.getData());
                form.findField('c_value').setValue(content_editor.getData());
                //console.log(form.findField('c_value').getValue());
            }
            form.submit({
                // params: Ext.util.JSON.encode(form.getValues()),
                method: 'PUT',
                waitTitle:'訊息',
                waitMsg:'修改資料中',
                url:'http://dev.finpo.com.tw/posh/public/b/content/'+cId,

                success:function(form,action){

                    var store  = Ext.getCmp('contentgridpanel').getViewModel().getStore('ContentStore');
                    store.proxy.url='http://dev.finpo.com.tw/posh/public/b/content';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','文案修改成功', function(){
                        var window = Ext.getCmp('contentwindow');
                        window.close();
                    });

                },
                failure:function(form,action){

                   Ext.Msg.alert('訊息','管理者新增失敗');

                }

            });
        }
    },

    onContentFormAfterRender: function(component, eOpts) {
        var cId = Ext.getCmp('c_id').getValue();

        Ext.Ajax.request({
            url: 'http://dev.finpo.com.tw/posh/public/b/content/'+cId,
            success: function(response, opts){
                var obj = Ext.JSON.decode(response.responseText);
                cValue = obj.data.c_value;
                //console.log(cValue);
                var form = Ext.getCmp('contentForm').getForm();
                form.setValues(obj.data);
                if(obj.data.c_key !== 'product_lastdate' && obj.data.c_key !== 'line_link'){
                    Ext.getCmp('cKeyTemp').setValue(obj.data.c_key);
                    content_editor = CKEDITOR.replace('ckreplace');
                    content_editor.setData(cValue);
                }else{
                    Ext.getCmp('cKeyTemp').setValue(obj.data.c_key);
                    Ext.getCmp('cValueTemp').setValue(obj.data.c_value);
                    //console.log(Ext.getCmp('cValueTemp').getValue());
                }

            },
            failure: function(response, opts){
                console.log('server-side failure with status code ' + response.status);
            }
        });
    }

});