Ext.define('Target.view.NewsWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.newswindow',

    requires: [
        'Target.view.NewsWindowViewModel',
        'Ext.form.field.Hidden',
        'Ext.form.Panel',
        'Ext.form.field.TextArea',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'newswindow'
    },
    height: 420,
    id: 'newswindow',
    width: 720,
    layout: 'fit',
    modal: true,
    y: 150,
    defaultListenerScope: true,

    items: [
        {
            xtype: 'hiddenfield',
            id: 'nm_id',
            fieldLabel: 'Label',
            name: 'nm_id'
        },
        {
            xtype: 'form',
            id: 'newsForm',
            bodyPadding: 10,
            items: [
                {
                    xtype: 'textfield',
                    anchor: '100%',
                    id: 'nm_title',
                    fieldLabel: '標題',
                    name: 'nm_title'
                },
                {
                    xtype: 'textareafield',
                    anchor: '98%',
                    formBind: false,
                    id: 'ckreplace',
                    name: 'nm_body'
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    layout: {
                        type: 'hbox',
                        pack: 'end'
                    },
                    items: [
                        {
                            xtype: 'button',
                            text: '修改',
                            listeners: {
                                click: 'onButtonClick'
                            }
                        }
                    ]
                }
            ],
            listeners: {
                afterrender: 'onContentFormAfterRender'
            }
        }
    ],

    onButtonClick: function(button, e, eOpts) {
        var Id = Ext.getCmp('nm_id').getValue();
        var form = Ext.getCmp('newsForm').getForm();
        if(form.isValid()){
            form.findField('nm_body').setValue(content_editor.getData());
            form.submit({
                method: 'PUT',
                waitTitle:'訊息',
                waitMsg:'修改資料中',
                url:'http://dev.finpo.com.tw/degi-api/target-local/public/b/news/'+Id,

                success:function(form,action){

                    var store  = Ext.getCmp('newsgridpanel').getViewModel().getStore('NewsStore');
                    store.proxy.url='http://dev.finpo.com.tw/degi-api/target-local/public/b/news';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','最新消息修改成功', function(){
                        var window = Ext.getCmp('newswindow');
                        window.close();
                    });

                },
                failure:function(form,action){

                   Ext.Msg.alert('訊息','最新消息修改失敗');

                }

            });
        }
    },

    onContentFormAfterRender: function(component, eOpts) {
        var nId = Ext.getCmp('nm_id').getValue();

        Ext.Ajax.request({
            url: 'http://dev.finpo.com.tw/degi-api/target-local/public/b/news/'+nId,
            success: function(response, opts){
                var obj = Ext.JSON.decode(response.responseText);
                Value = obj.data.nm_body;
                //console.log(Value);
                var form = Ext.getCmp('newsForm').getForm();
                form.setValues(obj.data);
                content_editor = CKEDITOR.replace('ckreplace');
                content_editor.setData(Value);
            },
            failure: function(response, opts){
                console.log('server-side failure with status code ' + response.status);
            }
        });
    }

});