Ext.define('Target.view.OrderWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.orderwindow',

    requires: [
        'Target.view.OrderWindowViewModel',
        'Ext.form.field.Hidden',
        'Ext.form.Panel',
        'Ext.form.field.TextArea',
        'Ext.form.field.Display',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'orderwindow'
    },
    height: 500,
    id: 'orderwindow',
    maxWidth: 800,
    width: 640,
    title: 'My Window',
    defaultListenerScope: true,

    items: [
        {
            xtype: 'hiddenfield',
            id: 'o_id',
            fieldLabel: 'Label',
            name: 'o_id'
        },
        {
            xtype: 'form',
            id: 'orderForm',
            bodyPadding: 10,
            title: '',
            layout: {
                type: 'vbox',
                align: 'stretch'
            },
            items: [
                {
                    xtype: 'textareafield',
                    height: 160,
                    fieldLabel: '商品內容',
                    name: 'o_detail',
                    editable: false
                },
                {
                    xtype: 'displayfield',
                    fieldLabel: '會員ID',
                    name: 'm_id',
                    value: 'Display Field'
                },
                {
                    xtype: 'displayfield',
                    fieldLabel: '訂單成立日期',
                    name: 'o_date',
                    value: 'Display Field'
                },
                {
                    xtype: 'textareafield',
                    height: 160,
                    fieldLabel: 'Paypal訊息',
                    name: 'o_paypal',
                    value: 'Display Field',
                    editable: false
                },
                {
                    xtype: 'textfield',
                    fieldLabel: '狀態',
                    name: 'o_status'
                }
            ]
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'bottom',
            layout: {
                type: 'hbox',
                pack: 'end'
            },
            items: [
                {
                    xtype: 'button',
                    text: '修改',
                    listeners: {
                        click: 'onButtonClick'
                    }
                }
            ]
        }
    ],
    listeners: {
        afterrender: 'onWindowAfterRender'
    },

    onButtonClick: function(button, e, eOpts) {
        var form = Ext.getCmp('orderForm').getForm();

        if(form.isValid()){
            var oId = Ext.getCmp('o_id').getValue();
            form.submit({
                // params: Ext.util.JSON.encode(form.getValues()),
                method: 'PUT',
                waitTitle:'訊息',
                waitMsg:'修改資料中',
                url:'http://dev.finpo.com.tw/posh/public/b/order/'+oId,

                success:function(form,action){

                    var store  = Ext.getCmp('ordergridpanel').getViewModel().getStore('OrderStore');
                    store.proxy.url='http://dev.finpo.com.tw/posh/public/b/order';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','訂單修改成功', function(){
                        var window = Ext.getCmp('orderwindow');
                        window.close();
                    });

                },
                failure:function(form,action){

                   Ext.Msg.alert('訊息','訂單修改失敗');

                }

            });
        }
    },

    onWindowAfterRender: function(component, eOpts) {
        var oId = Ext.getCmp('o_id').getValue();

        Ext.Ajax.request({
            url: 'http://dev.finpo.com.tw/posh/public/b/order/'+oId,
            success: function(response, opts){
                var obj = Ext.decode(response.responseText);
                var form = Ext.getCmp('orderForm').getForm();
                form.setValues(obj.data);

                var oDetail = Ext.JSON.decode(obj.data.o_detail);
                var oDetailEval = JSON.stringify(oDetail, null, 4);
                //console.log(oDetailEval);
                form.findField('o_detail').setValue(oDetailEval);

                var oPaypal = Ext.JSON.decode(obj.data.o_paypal);
                var oPaypalEval = JSON.stringify(oPaypal, null, 4);
                //console.log(oPaypalEval);
                form.findField('o_paypal').setValue(oPaypalEval);
            },
            failure: function(response, opts){
                console.log('server-side failure with status code ' + response.status);
            }

        });
    }

});