Ext.define('Target.view.ProductGridPanel', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.productgridpanel',

    requires: [
        'Target.view.ProductGridPanelViewModel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.button.Button',
        'Ext.menu.Menu',
        'Ext.menu.Item',
        'Ext.toolbar.Paging',
        'Ext.selection.CheckboxModel'
    ],

    viewModel: {
        type: 'productgridpanel'
    },
    id: 'productgridpanel',
    title: 'My Grid Panel',
    defaultListenerScope: true,

    bind: {
        store: '{ProductStore}'
    },
    columns: [
        {
            xtype: 'gridcolumn',
            dataIndex: 'p_name',
            text: '名稱',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'p_price',
            text: '價格',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'p_year',
            text: '循環繳費次數',
            flex: 0.8
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'p_created_date',
            text: '建立時間',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'p_last_date',
            text: '最後修改',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            renderer: function(value, metaData, record, rowIndex, colIndex, store, view) {
                if( value == 'open' ){
                    return '啟用';
                }else{
                    return '停用';
                }
            },
            dataIndex: 'p_status',
            text: '啟／停用',
            flex: 0.5
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'top',
            items: [
                {
                    xtype: 'button',
                    text: '功能',
                    menu: {
                        xtype: 'menu',
                        items: [
                            {
                                xtype: 'menuitem',
                                text: '新增',
                                listeners: {
                                    click: 'onMenuitemClick'
                                }
                            },
                            {
                                xtype: 'menuitem',
                                text: '修改',
                                listeners: {
                                    click: 'onMenuitemClick1'
                                }
                            }
                        ]
                    }
                }
            ]
        },
        {
            xtype: 'pagingtoolbar',
            dock: 'bottom',
            width: 360,
            displayInfo: true,
            bind: {
                store: '{ProductStore}'
            }
        }
    ],
    selModel: {
        selType: 'checkboxmodel',
        mode: 'SINGLE'
    },

    onMenuitemClick: function(item, e, eOpts) {
        /* 新增商品 */
        var window = Ext.create('Posh.view.ProductWindow');

        window.setConfig('title', '新增商品');

        Ext.getCmp('productUpdateBtn').setVisible(false);
        Ext.getCmp('productAddBtn').setVisible(true);

        window.show();
    },

    onMenuitemClick1: function(item, e, eOpts) {
        /* 修改商品 */
        var selmodel = Ext.getCmp('productgridpanel').getSelectionModel();
        var count = selmodel.getCount();

        if(count !== 0){
            var seldata = selmodel.getSelection();
            var window = Ext.create('Posh.view.ProductWindow');

            Ext.getCmp('p_id').setValue(seldata[0].data.p_id);
            window.setConfig('title', '修改商品');


            Ext.Ajax.request({
                url: 'http://dev.finpo.com.tw/posh/public/b/product/'+seldata[0].data.p_id,
                success: function(response, opts){
                    var obj = Ext.decode(response.responseText);

                    Ext.getCmp('productForm').getForm().setValues(obj.data);
                },
                failure: function(response, opts){
                    console.log('server-side failure with status code ' + response.status);
                }

            });

            Ext.getCmp('productAddBtn').setVisible(false);
            Ext.getCmp('productUpdateBtn').setVisible(true);

            window.show();
        }else{
            Ext.Msg.alert('訊息', '請選擇一個商品修改');
        }

    }

});