Ext.define('Target.view.ProductWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.productwindow',

    requires: [
        'Target.view.ProductWindowViewModel',
        'Ext.form.Panel',
        'Ext.form.field.Hidden',
        'Ext.form.field.Number',
        'Ext.form.RadioGroup',
        'Ext.form.field.Radio',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'productwindow'
    },
    height: 250,
    id: 'productwindow',
    width: 400,
    title: 'My Window',
    defaultListenerScope: true,

    items: [
        {
            xtype: 'form',
            id: 'productForm',
            bodyPadding: 10,
            title: '',
            items: [
                {
                    xtype: 'hiddenfield',
                    anchor: '100%',
                    id: 'p_id',
                    fieldLabel: 'Label',
                    name: 'p_id'
                },
                {
                    xtype: 'textfield',
                    anchor: '.8',
                    fieldLabel: '名稱',
                    name: 'p_name'
                },
                {
                    xtype: 'numberfield',
                    anchor: '.8',
                    fieldLabel: '價格',
                    name: 'p_price'
                },
                {
                    xtype: 'numberfield',
                    anchor: '.8',
                    fieldLabel: '循環繳費次數',
                    name: 'p_year'
                },
                {
                    xtype: 'radiogroup',
                    anchor: '.8',
                    fieldLabel: '啟／停用',
                    items: [
                        {
                            xtype: 'radiofield',
                            name: 'p_status',
                            boxLabel: '啟用',
                            inputValue: 'open'
                        },
                        {
                            xtype: 'radiofield',
                            name: 'p_status',
                            boxLabel: '停用',
                            inputValue: 'close'
                        }
                    ]
                }
            ]
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'bottom',
            layout: {
                type: 'hbox',
                pack: 'end'
            },
            items: [
                {
                    xtype: 'button',
                    id: 'productAddBtn',
                    text: '新增',
                    listeners: {
                        click: 'onProductAddBtnClick'
                    }
                },
                {
                    xtype: 'button',
                    id: 'productUpdateBtn',
                    text: '修改',
                    listeners: {
                        click: 'onProductUpdateBtnClick'
                    }
                }
            ]
        }
    ],

    onProductAddBtnClick: function(button, e, eOpts) {
        var form = Ext.getCmp('productForm').getForm();

        if(form.isValid()){

            form.submit({

                waitTitle:'訊息',
                waitMsg:'新增資料中',
                url:'http://dev.finpo.com.tw/posh/public/b/product',

                success:function(form,action){

                    var store  = Ext.getCmp('productgridpanel').getViewModel().getStore('ProductStore');
                    store.proxy.url='http://dev.finpo.com.tw/posh/public/b/product';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','產品新增成功');

                },
                failure:function(form,action){

                   Ext.Msg.alert('訊息','產品新增失敗');

                }

            });
        }
    },

    onProductUpdateBtnClick: function(button, e, eOpts) {
        var form = Ext.getCmp('productForm').getForm();

        if(form.isValid()){
            var pId = Ext.getCmp('p_id').getValue();
            form.submit({
                // params: Ext.util.JSON.encode(form.getValues()),
                method: 'PUT',
                waitTitle:'訊息',
                waitMsg:'修改資料中',
                url:'http://dev.finpo.com.tw/posh/public/b/product/'+pId,

                success:function(form,action){

                    var store  = Ext.getCmp('productgridpanel').getViewModel().getStore('ProductStore');
                    store.proxy.url='http://dev.finpo.com.tw/posh/public/b/product';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','產品修改成功', function(){
                        var window = Ext.getCmp('productwindow');
                        window.close();
                    });

                },
                failure:function(form,action){

                   Ext.Msg.alert('訊息','產品修改失敗');

                }

            });
        }
    }

});