Ext.define('Target.view.SystemVariableGridPanel', {
    extend: 'Ext.grid.Panel',
    alias: 'widget.systemvariablegridpanel',

    requires: [
        'Target.view.SystemVariableGridPanelViewModel',
        'Ext.grid.View',
        'Ext.selection.CheckboxModel',
        'Ext.grid.column.Column',
        'Ext.form.field.Text',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'systemvariablegridpanel'
    },
    id: 'systemvariablegridpanel',
    defaultListenerScope: true,

    bind: {
        store: '{SystemVariableStore}'
    },
    selModel: {
        selType: 'checkboxmodel'
    },
    columns: [
        {
            xtype: 'gridcolumn',
            dataIndex: 'sv_name',
            text: '變數說明',
            flex: 1
        },
        {
            xtype: 'gridcolumn',
            dataIndex: 'sv_value',
            text: '變數數值',
            flex: 1,
            editor: {
                xtype: 'textfield'
            }
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'top',
            items: [
                {
                    xtype: 'button',
                    text: '修改',
                    listeners: {
                        click: 'onButtonClick'
                    }
                }
            ]
        }
    ],

    onButtonClick: function(button, e, eOpts) {
        var selmodel = Ext.getCmp('systemvariablegridpanel').getSelectionModel();
        var count = selmodel.getCount();

        if(count !== 0){
            var seldata = selmodel.getSelection();

            var window = Ext.create('Target.view.SystemVariableWindow');

            Ext.getCmp('sv_id').setValue(seldata[0].data.sv_id);
            Ext.getCmp('sv_value').setValue(seldata[0].data.sv_value);
            Ext.getCmp('sv_value').setConfig('fieldLabel',seldata[0].data.sv_name);

            window.setConfig('title', '修改全站變數');

            window.show();
        }else{
            Ext.Msg.alert('訊息', '請選擇一個全站變數修改');
        }
    }

});