Ext.define('Target.view.SystemVariableWindow', {
    extend: 'Ext.window.Window',
    alias: 'widget.systemvariablewindow',

    requires: [
        'Target.view.MemberWindowViewModel1',
        'Ext.form.field.Hidden',
        'Ext.form.Panel',
        'Ext.form.field.TextArea',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'systemvariablewindow'
    },
    height: 194,
    id: 'SystemVariableWindow',
    width: 413,
    modal: true,
    defaultListenerScope: true,

    layout: {
        type: 'vbox',
        align: 'stretch'
    },
    items: [
        {
            xtype: 'hiddenfield',
            flex: 1,
            id: 'sv_id',
            name: 'sv_id'
        },
        {
            xtype: 'form',
            id: 'SystemVariableForm',
            bodyPadding: 10,
            layout: {
                type: 'vbox',
                align: 'stretch'
            },
            items: [
                {
                    xtype: 'textareafield',
                    flex: 1,
                    height: 104,
                    id: 'sv_value',
                    fieldLabel: '',
                    name: 'sv_value',
                    allowBlank: false
                }
            ]
        },
        {
            xtype: 'button',
            flex: 1,
            height: 44,
            text: '修改',
            listeners: {
                click: 'onButtonClick'
            }
        }
    ],

    onButtonClick: function(button, e, eOpts) {
        var form = Ext.getCmp('SystemVariableForm').getForm();

        if(form.isValid()){

            form.submit({
                method: 'PUT',
                waitTitle:'訊息',
                waitMsg:'修改資料中',
                url:'http://dev.finpo.com.tw/degi-api/target-local/public/b/system_variable/'+Ext.getCmp('sv_id').getValue(),
                success:function(form,action){
                    var store  = Ext.getCmp('systemvariablegridpanel').getViewModel().getStore('SystemVariableStore');
                    store.proxy.url='http://dev.finpo.com.tw/degi-api/target-local/public/b/system_variable';
                    store.load();

                    form.reset();
                    Ext.Msg.alert('訊息','全站變數修改成功', function(){
                        var window = Ext.getCmp('SystemVariableWindow');
                        window.close();
                    });
                },
                failure:function(form,action){
                    Ext.Msg.alert('訊息','全站變數修改失敗');
                }
            });
        }
    }

});